import React, { useState } from 'react';

const PostDataToApi = () => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [headerData, setHeaderData] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('https://chimpu.xyz/api/post.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ phonenumber: phoneNumber })
      });

      if (!response.ok) {
        throw new Error('Failed to post data to API');
      }

      const responseData = await response.text();
      const headers = response.headers;

      // Access data from headers
      const yourHeaderData = headers.get('Your-Header-Name'); // Replace 'Your-Header-Name' with the actual header name

      // Update state with header data
      setHeaderData(yourHeaderData);
      setError('');
    } catch (error) {
      console.error('Error:', error.message);
      setError(error.message);
      setHeaderData('');
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Phone Number:
          <input
            type="text"
            value={phoneNumber}
            onChange={(e) => setPhoneNumber(e.target.value)}
          />
        </label>
        <button type="submit">Submit</button>
      </form>
      {error && (
        <div>
          <p>Error: {error}</p>
        </div>
      )}
      {headerData && (
        <div>
          <h2>Data received in headers:</h2>
          <p>{headerData}</p>
        </div>
      )}
    </div>
  );
};

export default PostDataToApi;
