import React, { useState, useEffect } from 'react';

const PostDataToApi = () => {
  const [phoneNumber, setPhoneNumber] = useState('');
  const [responseData, setResponseData] = useState(null);
  const [error, setError] = useState(null);

  // Load phone number from local storage on component mount
  useEffect(() => {
    const savedPhoneNumber = localStorage.getItem('phoneNumber');
    if (savedPhoneNumber) {
      setPhoneNumber(savedPhoneNumber);
    }
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('https://chimpu.xyz/api/post.php', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ phonenumber: phoneNumber })
      });

      if (response.ok) {
        const data = await response.json();
        setResponseData(data);
        setError(null);

        // Save phone number to local storage
        localStorage.setItem('phoneNumber', phoneNumber);
      } else {
        const errorData = await response.json();
        setError(errorData);
        setResponseData(null);
      }
    } catch (error) {
      console.error('Error:', error);
      setError({ error: true, message: 'An error occurred while fetching data' });
      setResponseData(null);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Phone Number:
          <input
            type="text"
            value={phoneNumber}
            onChange={(e) => setPhoneNumber(e.target.value)}
          />
        </label>
        <button type="submit">Submit</button>
      </form>
      {error && (
        <div>
          <p>Error: {error.message}</p>
        </div>
      )}
      {responseData && (
        <div>
          <h2>Data received:</h2>
          <p>Phone Number: {phoneNumber}</p>
        </div>
      )}
    </div>
  );
};

export default PostDataToApi;
